var checkbox=document.querySelectorAll(".checkbox label input[type=checkbox]");if(0<checkbox.length)for(var i=0;i<checkbox.length;i++)checkbox[i].addEventListener("click",CheckboxMarkAsChecked,!0);var radio=document.querySelectorAll(".radio label input[type=radio]");if(0<radio.length)for(i=0;i<checkbox.length;i++)radio[i].addEventListener("click",RadioMarkAsChecked,!0);
function CheckboxMarkAsChecked(a){0==a.target.checked?(a.target.parentNode.parentNode.classList.remove("checkbox-checked"),this.removeAttribute("checked")):(a.target.parentNode.parentNode.classList.add("checkbox-checked"),this.setAttribute("checked",1))}
function RadioMarkAsChecked(a){var b=a.target.getAttribute("name");document.querySelectorAll('input[name="'+b+'"]').forEach(function(a,b){a.parentNode.parentNode.classList.contains("radio-checked")&&(a.parentNode.parentNode.classList.remove("radio-checked"),a.removeAttribute("checked"))});a.target.parentNode.parentNode.classList.add("radio-checked");this.setAttribute("checked",1)}
function Reload(){var a=document.querySelectorAll(".checkbox");if(0<a.length)for(var b=0;b<a.length;b++){if(0<a[b].childNodes)var c=a[b].childNodes[0].childNodes[0].hasAttribute("checked");c&&a[b].classList.add("checkbox-checked")}a=document.querySelectorAll(".radio");if(0<a.length)for(b=0;b<a.length;b++){if(0<a[b].childNodes)var d=a[b].childNodes[0].childNodes[0].hasAttribute("checked");d&&a[b].classList.add("radio-checked")}}window.onload=function(){Reload()};
